import React, {useState,useEffect}  from "react";
import { Container, Row, Col, Form , Button} from "react-bootstrap";
import Photocontact from "../../assets/img/top-logo.svg";

import "./contact.scss";


const Contact = () => {

    const [validated, setValidated] = useState(false)
    const [error, setError] = useState(false)
    const [dataForm,setDataForm] =useState({})

 
    const handleChange =(e) => {
        setDataForm((prevData)=>{
         
            return{
                ...prevData,
                [e.target.name] : e.target.value,
            }
        })
       
       
    }

    const handleSubmit =(e)=> {
        e.preventDefault();
         const form = e.currentTarget;
        console.log("form.checkValidity()",form.checkValidity())
        setValidated(true);


        if (form.checkValidity() === false) {
            setError(true)
        
        } else{
            
            setError(false)
            


            form.reset();
            setValidated(false);
        }
    }

 
    


    return (
        <section id="contact">
            <div className="fondo">

            <Container>
                <Row>
                    <Col md={12000} lg={5000}>
                        <h3 className="Title-contact">
                            Get in touch<br></br>
                            <span className="Title-orange"> We are hiring!</span>
                        </h3>
                        <Form noValidate validated={validated} onSubmit={(e)=> handleSubmit(e)}>
                            <Form.Group  >
                               <Form.Control onChange={(e)=> handleChange(e) } requiered type="text" placeholder="Name" name="" />
                            </Form.Group>
                            <Form.Group  >
                               <Form.Control type="email" placeholder="Email" name="" />
                            </Form.Group>
                            <Form.Group  >
                               <Form.Control type="phone" placeholder="Phone" name="" />
                            </Form.Group>
                            <Form.Group >

                                <Form.Control as="textarea" rows={3} placeholder="Mensaje" name="message" />
                            </Form.Group>
                            <Button  type="submit">Enviar</Button>
                        </Form>

                        <br />

                    </Col>
                    <Col md={12} lg={7}>

                    
                        
                    </Col>
                </Row>
            </Container>
            </div>

            <div></div>

        </section >
     
    );
};

export default Contact
