import React, { useState, useEffect } from "react";
import { Container, Row, Col, Form, Button } from "react-bootstrap";
import Photocontact from "../../assets/img/contact-image.png";

import "./contact.scss";


const Contact = () => {

    const [validated, setValidated] = useState(false)
    const [error, setError] = useState(false)
    const [dataForm, setDataForm] = useState({})


    const handleChange = (e) => {
        setDataForm((prevData) => {

            return {
                ...prevData,
                [e.target.name]: e.target.value,
            }
        })


    }

    const handleSubmit = (e) => {
        e.preventDefault();
        const form = e.currentTarget;
        console.log("form.checkValidity()", form.checkValidity())
        setValidated(true);


        if (form.checkValidity() === false) {
            setError(true)

        } else {

            setError(false)



            form.reset();
            setValidated(false);
        }
    }





    return (
        <section id="contact">
            <div className="container-contacto container">

                <div>

                    <h3 className="Title-contact">
                        Get in touch<br></br>
                        <span className="Title-orange"> We are hiring!</span>
                    </h3>
                    <div className="form">
                    <Form noValidate validated={validated} onSubmit={(e) => handleSubmit(e)}>
                        <Form.Group  >
                            <Form.Control className="form-item" onChange={(e) => handleChange(e)} requiered type="text" placeholder="Name" name="" />
                        </Form.Group>
                        <Form.Group  >
                            <Form.Control className="form-item" type="email" placeholder="Email" name="" />
                        </Form.Group>
                        <Form.Group  >
                            <Form.Control className="form-item" type="phone" placeholder="Phone" name="" />
                        </Form.Group>
                        <Form.Group >

                            <Form.Control className="form-item" as="textarea"  rows={3} placeholder="Mensaje" name="message" />
                        </Form.Group>
                        <Button type="submit">Enviar</Button>
                    </Form>
                    </div>
                    <br />



                </div>






                <div>

                    <img className="FotosGlobo" src={Photocontact} alt="imagen de contacto" />
                </div>

            </div>

        </section >

    );
};

export default Contact
