import React, {useState,useEffect}  from "react";
import { Container, Row, Col, Form , Button} from "react-bootstrap";


const Contact = () => {

    const [validated, setValidated] = useState(false)
    const [dataForm,setDataForm] =useState({})

 
    const handleChange =(e) => {
        setDataForm((prevData)=>{
         
            return{
                ...prevData,
                [e.target.name] : e.target.value,
            }
        })
       
       
    }


    useEffect (()=>{
     console.log("dataForm:",dataForm)
    },[dataForm])
    


    return (
        <section id="contact">
            <Container>
                <Row>
                    <Col md={12} lg={5}>
                        <h3>
                            Get in touch<br></br>
                            <span> We are hiring!</span>
                        </h3>
                        <Form noValidate validated={validated} onSubmit={()=> handlerSubmit()}>
                            <Form.Group  >
                               <Form.Control onChange={(e)=> handleChange(e) } requiered type="text" placeholder="Name" name="" />
                            </Form.Group>
                            <Form.Group  >
                               <Form.Control type="email" placeholder="Email" name="" />
                            </Form.Group>
                            <Form.Group  >
                               <Form.Control type="phone" placeholder="Phone" name="" />
                            </Form.Group>
                            <Form.Group >

                                <Form.Control as="textarea" rows={3} placeholder="Mensaje" name="message" />
                            </Form.Group>
                            <Button  type="submit">Enviar</Button>
                        </Form>

                        <br />

                    </Col>
                    <Col md={12} lg={7}>


                        <img src="../../assets/img/contact-image.png" alt="imagen de contacto" />
                    </Col>
                </Row>
            </Container>

        </section >

    );
};

export default Contact
